LOCK TABLES `uchome_task` WRITE;
/*!40000 ALTER TABLE `uchome_task` DISABLE KEYS */;
INSERT INTO `uchome_task` VALUES (1,1,'更新一下自己的头像','头像就是你在这里的个人形象。<br>设置好了可获得特别惊喜：<br>本站会自动寻找优秀的异性朋友推荐给您。<br>您不赶快试试？',37,0,'image/task/avatar.gif','avatar.php',0,0,0,20,1),(2,1,'将个人资料补充完整','把自己的个人资料填写完整吧。<br>这样您会被更多的朋友找到的，系统也会帮您找到朋友。',12,0,'image/task/profile.gif','profile.php',0,0,0,20,2),(3,1,'发表自己的第一篇日志','现在，就写下自己的第一篇日志吧。<br>与大家一起分享自己的生活感悟。',7,0,'image/task/blog.gif','blog.php',0,0,0,5,3),(4,1,'寻找并添加五位好友','有了好友，您发的日志、图片等会被好友及时看到并传播出去；<br>您也会在首页方便及时的看到好友的最新动态。<br>这会让您在这里的生活变得丰富多彩。',6,0,'image/task/friend.gif','friend.php',0,0,0,50,4),(5,1,'验证激活自己的邮箱','填写自己真实的邮箱地址并验证通过。<br>您可以在忘记密码的时候使用该邮箱取回自己的密码；<br>还可以及时接受站内的好友通知等等。<br>这对您十分有帮助和必要。',7,0,'image/task/email.gif','email.php',0,0,0,10,5),(6,1,'邀请10个新朋友加入','邀请一下自己的QQ好友或者邮箱联系人，让亲朋好友一起来加入我们吧。',0,0,'image/task/friend.gif','invite.php',0,0,0,100,6),(7,1,'领取每日积分大礼包','每天登录访问自己的主页，就可领取积分大礼包。<br>还可免费获赠本周《热门日志排行榜》导读一份。',102,0,'image/task/gift.gif','gift.php',0,0,86400,2,99);
/*!40000 ALTER TABLE `uchome_task` ENABLE KEYS */;
UNLOCK TABLES;

